mapper.select:
SELECT * FROM (select result1.*, rownum as rnum from (select admin_sale_id, store_name, TO_CHAR(cre_date, 'YYYY-MM-DD') AS cre_date, TO_CHAR(edit_date, 'YYYY-MM-DD') AS edit_date, sale_name, price, a_code, b_code, auto from tb_admin_sale
AND store_name LIKE '%' || #{store_name} || '%'
AND b_code IN
#{b_code}
order by admin_sale_id) result1 ) where rnum between #{startRowNum} and #{endRowNum}

SELECT order_id
FROM tb_order 
WHERE status = #{status}

SELECT order_detail_id, product_id, quantity, price, quantity*price as calprice
FROM tb_order_detail 
WHERE order_id = #{order_id}

SELECT product_id, product_name
FROM tb_class 
WHERE product_id = #{product_id}

SELECT class_id, price, status 
FROM tb_class 
WHERE status = #{status}

SELECT class_name 
FROM tb_class 
WHERE class_id = #{class_id}
